import base64
from datetime import date
from dateutil.relativedelta import relativedelta
from odoo import api, fields, models, _
from odoo.exceptions import AccessError, UserError, ValidationError
import datetime

GIF = b''

# To attach empty sheet
# GIF = b"R0lGODdhAQABAIAAAP///////ywAAAAAAQABAAACAkQBADs="

class ThreeSixtyDegree(models.Model):
	_name = 'threesixty.degree'
	_inherit = 'mail.thread'

	# name = fields.Char(string="Name")
	assignee_id = fields.Many2one('hr.department',string="Assignee",track_visibility="onchange")
	assignee_hod_id = fields.Many2one('hr.employee',string="Department HOD",track_visibility="onchange")

	hod_1_id = fields.Many2one('hr.department',string="HOD 1",track_visibility="onchange")
	dept_hod_1_id = fields.Many2one('hr.employee',string="Department HOD",track_visibility="onchange")

	hod_2_id = fields.Many2one('hr.department',string="HOD 2",track_visibility="onchange")
	dept_hod_2_id = fields.Many2one('hr.employee',string="Department HOD",track_visibility="onchange")

	state = fields.Selection([('draft', 'Draft'),('confirmed', 'Confirmed')],string='Status',default='draft',track_visibility="onchange")
	soft_copy = fields.Binary(string="Attachment",required=False)

	@api.multi
	def button_confirm(self):
		self.write({'state': 'confirmed'})
		# Mail Template
		attachment = self.env['ir.attachment'].create({'name':'360-Degree performance appraisal form.docx',
														'type': 'binary',
														'datas_fname': '360-Degree performance appraisal form.docx',
														# 'datas': str(self.soft_copy).encode('utf8')})
														'datas': GIF})

		template_id = self.env.ref('indglobal_360_degree.email_template_for_hod')
		template_id.attachment_ids = [(6,0,[attachment.id])]
		template_id.sudo().send_mail(self.id, force_send=True)

	@api.onchange('hod_1_id','hod_2_id','assignee_id')
	def onchange_hod_details(self):
		if self.assignee_id:
			assignee = self.env['hr.employee'].search([('department_id','=',self.assignee_id.id),('designation_id','=',99)])
			self.assignee_hod_id = assignee
		else:
			self.assignee_hod_id = ''

		if self.hod_1_id:
			hod_one = self.env['hr.employee'].search([('department_id','=',self.hod_1_id.id),('designation_id','=',99)])
			self.dept_hod_1_id = hod_one
		else:
			self.dept_hod_1_id = ''

		if self.hod_2_id:
			hod_two = self.env['hr.employee'].search([('department_id','=',self.hod_2_id.id),('designation_id','=',99)])
			self.dept_hod_2_id = hod_two
		else:
			self.dept_hod_2_id = ''

	# Unlink
	@api.multi
	def unlink(self):
		if self.filtered(lambda x:x.state not in 'draft'):
			raise UserError(_('You cannot delete the record which is not in draft !..'))
		return super(ThreeSixtyDegree, self).unlink()